sys_script.script:
(function executeRule(current, previous /*null when async*/) {
//	gs.addInfoMessage(current.comments);
//	gs.addInfoMessage(current.comments_and_work_notes);

    updateTasks();

    function updateTasks() {
        var appTask = new GlideRecord('x_dosny_bit_procur_bit_procurement_approval_memo_task');
        appTask.addQuery('requested_item', current.sys_id);
        //appTask.addQuery('active', true);
        appTask.query();
        while (appTask.next()) {
			gs.addInfoMessage("appTask: " + appTask.sys_id);
			gs.addInfoMessage("copying: " + current.comments);
            appTask.work_notes = 'Additional Comment added to Request: ' + current.comments;
			gs.addInfoMessage("copied: " + current.comments);		

            appTask.update();
			gs.addInfoMessage("updated");
        }
    }

})(current, previous);
