sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here

	gs.addErrorMessage("in audit filter");
	var rel_list = new GlideRecord('sys_ui_list_control');

	rel_list.addQuery('name', current.getTableName()); //Get the related lists controlls for this table

	rel_list.addQuery('related_list', 'REL:09e81282876120106e337f9719cb0b39'); //Get the related list control for this list

	rel_list.query();

	if(rel_list.next()){

		gs.addErrorMessage("Found related list " + rel_list.name);
		gs.addErrorMessage("Filter " + rel_list.edit_default_filter);	
		gs.addErrorMessage("omit if empty " + rel_list.omit_if_empty);

		//var filter = 'sys_id=javascript:GetRelCis(\"' + current.cmdb_ci.toString() + '\")^sys_class_name=cmdb_ci_service^ORsys_class_name=u_cmdb_service_application^ORsys_class_name=u_cmdb_service_environment^ORsys_class_nameINSTANCEOFcmdb_ci_server';


		var filter = 'type=policy^EQ';


		//rel_list.omit_if_empty = true;


		rel_list.edit_default_filter = filter;

		rel_list.update();
	}	

})(current, previous);

