sys_ui_action.script:
function copy() {
	var confirmResult = confirm('Existing Approval Memo fields may be overwritten. Do you want to continue?');
	if (confirmResult == true) {

		var p = g_form.getValue('bit_procurement');					
		var c = g_form.getValue('bit_procurement_cc_line_item');	   


		// Copy Request Justification to Approval Memo Summary
		g_form.setValue('summary', g_form.getValue('request_justification'));      

        // Copy Procurment (or CC Line Item) Number and Vendor to Approval Memo Service Now Request Number and Vendor fields
		if (c && p) {			
			alert ("Both Procurement and CC line Item populated, not copying procurement fields.");
		} else if (p) {					
              var pRef = g_form.getReference('bit_procurement', lookupP);	                  
		} else if (c) {			
              var cRef = g_form.getReference('bit_procurement_cc_line_item', lookupC);	              
		} 	
	}

	function lookupP(pRef){    	
        g_form.setValue('service_now_request_number', pRef.number.toString());
		g_form.setValue('vendor', pRef.vendor.toString());	
	}	
	function lookupC(cRef){    
        g_form.setValue('service_now_request_number',cRef.number.toString());	
        g_form.setValue('vendor', cRef.vendor.toString());	
    } 

}
