sys_ui_page.client_script:
function onCancel() {
    GlideDialogWindow.get().destroy();
    return false;
}

function getServiceId() {
    try {
        var form = document.forms['form.' + '${JS:sys_id}'];
        var service_sys_id = form["select_service"].value;
        if (!service_sys_id) {
            theText = getMessage('{0} is mandatory.').withValues([GlideModal.get().getPreference('sysparm_service_field_label')]);
            showError(theText);
            return false;
        }
        showLoadMask();
        return service_sys_id;
    } catch (ex) {
		return null;
	}
}

function showLoadMask() {
    var loadMask = document.getElementById('load_mask_container');
    loadMask.style.display = 'flex';
}

function submitDialog() {
    var handler = GlideModal.get().getPreference('handler');
    if (!handler)
        return false;

    var serviceSysId = getServiceId();
    if (!serviceSysId)
        return false;
    else
        return handler.execute(serviceSysId);
}

function cancelDialog() {
    var handler = GlideModal.get().getPreference('handler');
    if (!handler)
        return false;
    return handler.cancel(GlideModal.get().getPreference('sysparm_record_id'));
}

function showError(message){
	$j("#error").html(message);
	$j("#error").show();
	hideSpinner();
}
